var WebSocket=require("../lib/faye/websocket"),port=process.argv[2]||7e3,secure=process.argv[3]==="ssl",scheme=secure?"wss":"ws",ws=new WebSocket.Client(scheme+"://localhost:"+port+"/");console.log("Connecting to "+ws.url);ws.onopen=function(e){console.log("open");ws.send("Hello, WebSocket!")};ws.onmessage=function(e){console.log("message",e.data)};ws.onclose=function(e){console.log("close",e.code,e.reason)};