var noptify=require(".."),assert=require("assert");describe("Collectable",function(){it("provides the helper method to read from stdin",function(){var e=noptify();assert.ok(typeof e.stdin=="function","stdin defined")});it("is invoked only when .parse() is called",function(e){var t=noptify(["",""]),n="testing out stdin helper";t.stdin(function(t,r){assert.equal(r,n);e()});t.parse();process.nextTick(function(){process.stdin.emit("data",n);process.stdin.emit("end")})});it(".files()",function(e){var t=noptify(["","","test/fixtures/a.js","test/fixtures/b.js"]);t.parse();t.files(function(t,n){assert.equal(n,"a\nb\n");e()})})});