var noptify=require(".."),assert=require("assert");describe("Commandable",function(){it("provides the .command() utility",function(){assert.ok(typeof noptify().command=="function")});describe("Parses remaining arguments and route to the appropriate command",function(){it("can be a simple function",function(e){var t=noptify(["","","init","--debug","foo"]).option("debug","an option");t.command("init",function(t,n){assert.deepEqual(t,["--debug","foo"]);assert.equal(n.debug,!0);assert.equal(n.argv.remain[0],"foo");e()});t.parse()});it("or another program, an Noptify instance",function(e){var t=["","","init","--debug","myapp","foo"],n=noptify(t).option("debug","Debug output").command("myapp",e.bind(null,null));noptify(t).command("init",n).parse()})})});