/*
 * Tests to verify we're writing signed integers correctly
 */function test8(){var e=new Buffer(4);mod_ctype.wsint8(35,"big",e,0);mod_ctype.wsint8(35,"little",e,1);mod_ctype.wsint8(-5,"big",e,2);mod_ctype.wsint8(-5,"little",e,3);ASSERT.equal(35,e[0]);ASSERT.equal(35,e[1]);ASSERT.equal(251,e[2]);ASSERT.equal(251,e[3]);ASSERT.throws(function(){mod_ctype.wsint8(2748,"big",e,0)});ASSERT.throws(function(){mod_ctype.wsint8(2748,"little",e,0)})}function test16(){var e=new Buffer(6);mod_ctype.wsint16(35,"big",e,0);mod_ctype.wsint16(35,"little",e,2);ASSERT.equal(0,e[0]);ASSERT.equal(35,e[1]);ASSERT.equal(35,e[2]);ASSERT.equal(0,e[3]);mod_ctype.wsint16(-5,"big",e,0);mod_ctype.wsint16(-5,"little",e,2);ASSERT.equal(255,e[0]);ASSERT.equal(251,e[1]);ASSERT.equal(251,e[2]);ASSERT.equal(255,e[3]);mod_ctype.wsint16(-1679,"big",e,1);mod_ctype.wsint16(-1679,"little",e,3);ASSERT.equal(249,e[1]);ASSERT.equal(113,e[2]);ASSERT.equal(113,e[3]);ASSERT.equal(249,e[4])}function test32(){var e=new Buffer(8);mod_ctype.wsint32(35,"big",e,0);mod_ctype.wsint32(35,"little",e,4);ASSERT.equal(0,e[0]);ASSERT.equal(0,e[1]);ASSERT.equal(0,e[2]);ASSERT.equal(35,e[3]);ASSERT.equal(35,e[4]);ASSERT.equal(0,e[5]);ASSERT.equal(0,e[6]);ASSERT.equal(0,e[7]);mod_ctype.wsint32(-5,"big",e,0);mod_ctype.wsint32(-5,"little",e,4);ASSERT.equal(255,e[0]);ASSERT.equal(255,e[1]);ASSERT.equal(255,e[2]);ASSERT.equal(251,e[3]);ASSERT.equal(251,e[4]);ASSERT.equal(255,e[5]);ASSERT.equal(255,e[6]);ASSERT.equal(255,e[7]);mod_ctype.wsint32(-805306713,"big",e,0);mod_ctype.wsint32(-805306713,"litle",e,4);ASSERT.equal(207,e[0]);ASSERT.equal(255,e[1]);ASSERT.equal(254,e[2]);ASSERT.equal(167,e[3]);ASSERT.equal(167,e[4]);ASSERT.equal(254,e[5]);ASSERT.equal(255,e[6]);ASSERT.equal(207,e[7])}var mod_ctype=require("../../../ctio.js"),ASSERT=require("assert");test8();test16();test32();