var reduce=require("..");describe("reduce",function(){describe("when adding prev and current",function(){it("should be sum all the values",function(){var e=[2,2,2],t=function(e,t){return e+t},n=e.reduce(t),r=reduce(e,t);n.should.equal(6);r.should.equal(n)})});describe("when passing in an initial value",function(){it("should default to it",function(){var e=[],t=function(e){return e},n=e.reduce(t,"foo"),r=reduce(e,t,"foo");n.should.equal("foo");r.should.equal(n)});it("should start with it",function(){var e=[10,10],t=function(e,t){return e+t},n=e.reduce(t,10),r=reduce(e,t,10);n.should.equal(30);r.should.equal(n)})})});