var expect=require("expect.js"),_=require("underscore"),cheerio=require("../lib/cheerio"),xml=function(e,t){t=_.extend({xmlMode:!0},t);var n=cheerio.load(e,t);return n.xml()};describe("render",function(){describe("(xml)",function(){it("should render <media:thumbnail /> tags correctly",function(){var e='<media:thumbnail url="http://www.foo.com/keyframe.jpg" width="75" height="50" time="12:05:01.123" />';expect(xml(e)).to.equal('<media:thumbnail url="http://www.foo.com/keyframe.jpg" width="75" height="50" time="12:05:01.123"/>')});it("should render <link /> tags (RSS) correctly",function(){var e="<link>http://www.github.com/</link>";expect(xml(e)).to.equal("<link>http://www.github.com/</link>")})})});